package kudu;import org.kududb.ColumnSchema;import org.kududb.Schema;import org.kududb.Type;import org.kududb.client.KuduClient;import java.util.ArrayList;import java.util.List;/** * Created by Hiram on 2016/3/28. */public class CreateTable {	public static void main(String[] args) {		try {			createTable();		} catch (Exception e) {			e.printStackTrace();		}	}	public static void createTable() throws Exception {		String tableName = "MyTest";		KuduClient client = new KuduClient.KuduClientBuilder(Constant.KUDU_MASTER).build();		List<ColumnSchema> columns = new ArrayList(2);		columns.add(new ColumnSchema.ColumnSchemaBuilder("key", Type.INT32)				.key(true)				.encoding(ColumnSchema.Encoding.BIT_SHUFFLE)				.build());		columns.add(new ColumnSchema.ColumnSchemaBuilder("value", Type.STRING)				.compressionAlgorithm(ColumnSchema.CompressionAlgorithm.LZ4)				.build());		columns.add(new ColumnSchema.ColumnSchemaBuilder("intValue", Type.INT32)				.encoding(ColumnSchema.Encoding.BIT_SHUFFLE)				.build());		Schema schema = new Schema(columns);		client.createTable(tableName, schema);	}}