package kudu;import org.kududb.Schema;import org.kududb.client.*;import java.sql.Statement;import java.util.ArrayList;import java.util.List;/** * Created by Hiram on 2016/3/28. */public class Test {	public static void main(String[] args) {		KuduClient client = new KuduClient.KuduClientBuilder(Constant.KUDU_MASTER).build();		try {			KuduTable table = client.openTable(Constant.TABLE_NAME);			PartitionSchema partitionSchema = client.openTable(Constant.TABLE_NAME).getPartitionSchema();			List<PartitionSchema.HashBucketSchema> hashBucketSchemas = partitionSchema.getHashBucketSchemas();			KuduScanner scanner = client.newScannerBuilder(table).build();			long time = System.currentTimeMillis();			while (scanner.hasMoreRows()){				scanner.nextRows();			}			System.out.println(System.currentTimeMillis() - time);		} catch (Exception e) {			e.printStackTrace();		} finally {			try {				client.shutdown();			} catch (Exception e) {				e.printStackTrace();			}		}	}}